query getCompanyTest($id: ID!, $page: Int, $limit: Int, $pass: Boolean) {
  getCompanyTest(id: $id) {
    ...CompanyTestType
    attachedEvidences {
      evidence {
        id
        files
        fileNode {
          id
          discardedAt
          __typename
        }
        __typename
      }
      __typename
    }
    assertionResults(page: $page, limit: $limit, pass: $pass) {
      collection {
        ...AssertionResultType
        __typename
      }
      metadata {
        currentPage
        limitValue
        totalCount
        totalFailingAssertions(companyTestId: $id)
        totalAssertions(companyTestId: $id)
        totalPages
        __typename
      }
      __typename
    }
    __typename
  }
}

fragment CompanyTestType on CompanyTest {
  id
  pass
  enabled
  exportable
  disabledJustification
  discardedAt
  passedWithUploadJustification
  updatedAt
  lastEvaluated
  lastPassedAt
  enabledFieldUpdatedById
  enabledFieldUpdatedByUser
  firstFailedAt
  nextDueDate
  owner {
    id
    name
    imageUrl
    __typename
  }
  testV2 {
    ...TestV2Type
    __typename
  }
  resourceableType
  status
  toleranceWindowSeconds
  testIntervalSeconds
  __typename
}

fragment TestV2Type on TestV2 {
  id
  key
  title
  description
  assertionKey
  assertionData
  conditionKey
  conditionData
  reportKeys
  testDomain
  testFunction
  resourceCategory
  recommendedAction
  detailedRemediationSteps
  additionalInfo
  global
  vendor {
    id
    name
    __typename
  }
  author {
    id
    name
    imageUrl
    __typename
  }
  testType
  controls {
    id
    key
    name
    description
    report {
      key
      label
      __typename
    }
    __typename
  }
  __typename
}

fragment AssertionResultType on AssertionResult {
  id
  resourceable {
    ...FailingResourceType
    __typename
  }
  failMessage
  successMessage
  assertionKey
  pass
  data
  createdAt
  optional
  enabled
  disabledJustification
  __typename
}

fragment FailingResourceType on Resourceable {
  __typename
  ... on CompanyUser {
    id
    companyUserName: name
    imageUrl
    __typename
  }
  ... on Policy {
    id
    policyName: name
    __typename
  }
  ... on CloudResource {
    id
    owner {
      name
      __typename
    }
    vendor {
      name
      __typename
    }
    cloudResourceType
    region
    account
    thirdPartyId
    description
    name
    __typename
  }
  ... on Device {
    id
    deviceName
    serialNumber
    __typename
  }
  ... on Vendor {
    id
    vendorName: name
    __typename
  }
  ... on Repository {
    id
    repositoryName: name
    __typename
  }
  ... on CompanyUserVendor {
    id
    email
    username
    companyUser {
      id
      companyUserName: name
      imageUrl
      __typename
    }
    __typename
  }
  ... on Evidence {
    id
    evidenceType
    files
    fileNode {
      id
      name
      __typename
    }
    __typename
  }
  ... on CompanyTestAcknowledgement {
    id
    createdAt
    acknowledgedBy {
      name
      __typename
    }
    __typename
  }
  ... on PullRequest {
    id
    pullRequestName: name
    __typename
  }
  ... on ProductionBranch {
    id
    productionBranchName: name
    __typename
  }
  ... on CompanyControl {
    id
    control {
      key
      description
      __typename
    }
    __typename
  }
  ... on Ticket {
    id
    title
    openedAt
    __typename
  }
}
